// eslint-disable-next-line react/prop-types
export function FiftyPercent({ percentage }) {
  const divWidth = 48;
  const divHeight = 24;
  const borderRadius = 6; // Adjust the border radius as needed
  const text = `${percentage}%`;

  return (
    <div
      style={{
        width: divWidth,
        height: divHeight,
        borderRadius,
        border: '2px solid #6366F1', // Border color
        position: 'relative',
        backgroundColor: 'rgba(126, 135, 255, 0.8)', // Background color
      }}
    >
      <div
        style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          color: 'rgba(172, 179, 249, 1)', // Text color
          fontSize: '12px', // Adjust font size as needed
        }}
      >
        {text}
      </div>
    </div>
  );
}

